class dateCountdownWidget { constructor(date, widget){ this.date = date; this.widgetElem = widget; this.dateNumber = widget.querySelector(".countdown-numbers"); this.difference = this.calculateDateDifference(this.date); if(this.difference > 0){this.drawDateNumber(this.dateNumber, this.difference); } }; calculateDateDifference = date => { let deltaDays = 0; let currentDate = new Date(); let deltaTime = Math.abs(currentDate - date); deltaDays = Math.floor( deltaTime / (1000 * 60 * 60 *24) ); return deltaDays; }; drawDateNumber = ( numberElem, difference ) => { numberElem.innerHTML = difference; }  }